export interface Authorizations {
    /**
     * The broad category to which the product belongs. Valid values are \"Medical\",\"Dental\", \"Pharmacy\", \"FSA\", \"HRA\", \"HSA\", \"Vision\", \"Other\".
     */
    productCategory?: Authorizations.ProductCategoryEnum;
    /**
     * The unique Identification used to reference an episode of Authorization or Referral
     */
    referenceID?: string;
    /**
     * An episode of authorization or referral can have multiple types of service and each service type is uniquely identified using reference id and reference id seq. number
     */
    referenceIdSeqNo?: string;
    /**
     * The type of Authorization or Referral; values could be \"Authorization\", \"Referral\", \"Pre-Determination\", \"Inpatient Stay\"
     */
    referenceType?: Authorizations.ReferenceTypeEnum;
    /**
     * Unique ID of the provider requesting an authorization or referral.
     */
    requstingProviderId?: string;
    /**
     * Full Name of the Provider requesting Authorization or Referral
     */
    requestingProviderName?: string;
    /**
     * Unique ID of the provider to whom the member is being referred to by the provider requesting the referral.
     */
    referredToProviderId?: string;
    /**
     * Full Name of the Provider who is being Referred To by the Provider requesting a referral
     */
    referredToProviderName?: string;
    /**
     * The first date from which healthcare services are being requested for
     */
    requestedServicesFromDate?: string;
    /**
     * The last date till which healthcare services are being requested for.
     */
    requestedServicesToDate?: string;
    /**
     * Brief description of the healthcare services being requested for
     */
    serviceDescription?: string;
    /**
     * Number of Units of the healthcare service that were approved.  If status is still pending, value will be 0.
     */
    approvedUnits?: string;
    /**
     * Number of Units of the healthcare service that were used by the member.
     */
    usedUnits?: string;
    /**
     * Date on which the Status of the authorization or referral was last revised
     */
    statusDate?: string;
    /**
     * Status of the Authorization or Referral request; values could be \"Pending\", \"Denied\", \"Approved\"
     */
    status?: Authorizations.StatusEnum;
    /**
     * Any short comment associated with the particular authorization or referral, such as the reason for denial.
     */
    comments?: string;
    /**
     * Denotes whether the Authorization or Refferral is HIPAA sensitive; values can be either true or false.
     */
    hipaaSensitiveIndicator?: boolean;
}
