export interface ClaimLineDetail {
    /**
     * Line item sequence number.
     */
    claimLineId?: string;
    /**
     * Place of service Code.   This element is only included in the response for a medical claim where claimSubType = Medical and a dental claim where claimSubType = Dental
     */
    placeOfService?: string;
    /**
     * A short description of the place of service code.   This element is only included in the response for a medical claim where claimSubType = Medical and a dental claim where claimSubType = Dental
     */
    placeOfServiceDescription?: string;
    /**
     * Service from date.
     */
    serviceFromDate?: string;
    /**
     * Service to date.
     */
    serviceToDate?: string;
    /**
     * Procedure code for the claim line.
     */
    procedureCode?: string;
    /**
     * Short description of the procedure code.
     */
    procedureDescription?: string;
    /**
     * Primary diagnosis code.
     */
    primaryDiagnosisCode?: string;
    /**
     * Amount charged by the provider.
     */
    chargedAmount?: number;
    /**
     * Amount allowed by the payer.
     */
    allowedAmount?: number;
    /**
     * Amount difference between Charged Amount and Allowed amount that is usually attributed to a provider who is In-Network that is considered a \"Network Savings\" amount.
     */
    planDiscount?: number;
    /**
     * Plan Negotiated Rate
     */
    planNegotiatedRate?: number;
    /**
     * Amount paid by the payer.
     */
    paidAmount?: string;
    /**
     * Amount applied to the deductible of the member.
     */
    deductibleAmount?: number;
    /**
     * Amount applied to the copay of the member.
     */
    copayAmount?: number;
    /**
     * Amount applied to the coinsurance of the member.
     */
    coinsuranceAmount?: number;
    /**
     * Amount considered not covered by the payer. This is the patient portion.
     */
    notCoveredAmount?: number;
    /**
     * Number of units charged by the provider.
     */
    chargedUnits?: number;
    /**
     * Reason code for Not Covered or other detailed message.
     */
    claimDetailMessageCode?: string;
    /**
     * Description of why it is not covered or other detailed message.
     */
    claimDetailMessageDescription?: string;
    /**
     * Amount of patient responsibility for the claim Deductible + Copay + Coinsurance + Not Covered
     */
    patientResponsibility?: number;
    /**
     * The amount that an HRA contributed to payment of the patient portion.
     */
    hraPaidAmount?: number;
    /**
     * Revenue code for the claim line.    This element is only included in the response for a hospital claim where claimSubType = Hospital
     */
    revenueCode?: string;
    /**
     * A short description of the revenue code.     This element is only included in the response for a hospital claim where claimSubType = Hospital
     */
    revenueCodeDescription?: string;
    /**
     * toothSurface
     */
    toothSurface?: string;
    /**
     * toothNumber
     */
    toothNumber?: string;
    /**
     * toothChartGraphic (to be discussed)
     */
    toothChartGraphic?: string;
}
