export interface QuickViewCriteria {
    /**
     * List of provider Ids.
     */
    providerIds?: Array<string>;
    /**
     * Service Start Date.
     */
    serviceStartDate?: string;
    /**
     * Service End Date.
     */
    serviceEndDate?: string;
    /**
     * Date Range for listing the claim details i.e last 30 days or last 60 days e.t.c.
     */
    dateRange?: number;
    /**
     * Status of the claim.  Examples are 'Received', 'Pending', 'Finalized'. Values are validated against MDM (Master Data Management).
     */
    claimStatus?: string;
}
